const Config = require("config-watch");
 

config = new Config("config/my-config.json");
//config = new Config("./config/my-config.json");

config.on("change", (err, config) => {
    if (err) throw err;
    console.log("Config changed. The new config is:\n", config.get());
});

exports.getKey = function() {
  console.log("This is a message from the demo package:"+config.get('password'));
}

exports.get = function(name) {
  console.log("This is a message from the demo package:"+config.get(name));
}

