const Config = require("config-watch");
 

config = new Config("/tmp/my-config.json");
var config1 = require('config');

console.log("config is", config1);

 
console.log("Initial values k4 is:\n", config.get('k4'));
console.log("Initial values:\n", config1.get('password'));
config1=config;

console.log("Initial values of config:",config1.get());

config.on("change", (err, config) => {
    if (err) throw err;
    console.log("Config changed. The new config is:\n", config.get());
});

config1.on("change", (err, config) => {
    if (err) throw err;
    console.log("Config changed. The new config is:\n", config.get());
});
